mapper.select:
SELECT * FROM (SELECT tt.*, ROWNUM AS rowno FROM ( SELECT nvl(B.SKU_GROUP7,'') as store_id, a.sku as sku, nvl(b.ReservedField03,0) as safe_storage, SUM(A.QTY - A.QTYALLOCATED - A.QTYONHOLD - A.QTYMVIN - A.QTYMVOUT) as storage from inv_lot_loc_id a LEFT JOIN BAS_SKU B ON A.SKU = B.SKU and b.customerid=a.customerid LEFT JOIN INV_LOT_ATT D ON d.lotnum=a.lotnum LEFT JOIN VIEW_MULTIWAREHOUSE C ON A.LOCATIONID = C.LOCATIONID WHERE D.LOTATT08 = 'N'
and a.customerid = #{param.customerCode}
and c.warehouseid = #{param.warehouseCode}
and a.sku in
#{s}
and B.SKU_GROUP7 in
#{t}
group by B.SKU_GROUP7,a.sku,b.ReservedField03 order by SUM(A.QTY - A.QTYALLOCATED - A.QTYONHOLD - A.QTYMVIN - A.QTYMVOUT) desc ) tt WHERE ROWNUM <= #{param.end} ) table_alias WHERE table_alias.rowno >= #{param.offset}

